import React, { Component } from 'react';
import {	Link } from 'react-router-dom';
import { connect } from 'react-redux';
import { userActions } from '../../actions';
import { Button, Form, Grid, Header, Image, Message,
	Segment, Loader } from 'semantic-ui-react'

class Login extends Component {
	constructor(props) {
		super(props);
		this.props.logout();
		this.state = {
			email: '',
			password: '',
			submitted: false
		};
		this.handleInputChange = this.handleInputChange.bind(this);
		this.submitLogin = this.submitLogin.bind(this);

	}

	handleInputChange(e) {
		let name = e.target.name;
		let value = e.target.value;
		this.setState({
			[name]: value
		});
	}

	submitLogin(e) {
		e.preventDefault();
		this.setState({ submitted: true });
		const { email, password } = this.state;
		if (email && password) {
			this.props.login(email, password);
		}
	}

	render(){
		const { loggingIn } = this.props;
		const { email, password, submitted } = this.state;
		return(
			<div className="register-form">
        <Grid textAlign='center' style={{ height: '100%' }} verticalAlign='middle'>
          <Grid.Column style={{ maxWidth: 450 }}>

            <Link to='/'>
              <Image src='/assets/images/logo.png' size='tiny' centered />
            </Link>

            <Header as='h2' color='teal' textAlign='center'>
              Create Account
            </Header>

            <Form size='large' onSubmit={this.onSubmit}>
              <Segment stacked>
                <Form.Input
				className={'form-group' + (submitted && !email ? ' has-error' : '')}
				placeholder="Enter Email" value={this.state.email} onChange={this.handleInputChange} name="email"
                  fluid
                //   placeholder='First Name'
                //   name='firstName'
                //   defaultValue={firstName}
                //   error={errors.firstName ? true : false}
                //   onChange={this.onChange}
                />
                <Form.Input
				className={'form-group' + (submitted && !email ? ' has-error' : '')}
				placeholder="Enter Email" value={this.state.email} onChange={this.handleInputChange} name="email"
                  fluid
                />
                <Form.Input
                  fluid
                  placeholder='E-mail'
                  name='email'
                  defaultValue={email}
                  error={errors.email ? true : false}
                  onChange={this.onChange}
                />
                <Form.Input
                  fluid
                  placeholder='Password'
                  name='password'
                  type='password'
                  defaultValue={password}
                  error={errors.password ? true : false}
                  onChange={this.onChange}
                />
                <Form.Input
                  fluid
                  placeholder='Confirm Password'
                  name='confirmPassword'
                  type='password'
                  defaultValue={confirmPassword}
                  error={errors.confirmPassword ? true : false}
                  onChange={this.onChange}
                />
                <Button color='teal' fluid size='large' disabled={isLoading}>
                  {!isLoading
                    ? 'Create Account'
                    : <Loader active inverted inline size='small' />
                  }
                </Button>
              </Segment>
            </Form>

            <Message>
              Already have an account? <Link to='/login'>Login</Link>
            </Message>

          </Grid.Column>
        </Grid>
      </div>
			// <div className="col-md-4 col-md-offset-4">
		 	// 	<h2 className="text-center">User Login</h2>
			// 	 	<form name="form">
			// 			<div className={'form-group' + (submitted && !email ? ' has-error' : '')}>
			// 				<label for="email">Email:</label>
			// 				<input type="text" id="email" className="form-control input-shadow" placeholder="Enter Email" value={this.state.email} onChange={this.handleInputChange} name="email"/>
			// 				{submitted && !email && <div className="help-block">Email is required</div> }
			// 			</div>
			// 			<div className={'form-group' + (submitted && !password ? ' has-error' : '')}>                    
			// 				<label>Password: </label>
			// 				<input type="password" id="exampleInputPassword" className="form-control input-shadow" placeholder="Enter Password" value={this.state.password}	onChange={this.handleInputChange}	name="password"/>
			// 				{submitted && !email && <div className="help-block">Password is required</div> }
			// 			</div>

			// 			<button type="button" onClick={this.submitLogin} className="btn btn-primary btn-block">Sign In</button>
			// 			<Link to="/register" className="btn btn-link">Register</Link>
			// 		</form>
			//  </div>

		)
	}
}
function mapState(state) {
	const { loggingIn } = state.authentication;
	return { loggingIn };
}

const actionCreators = {
	login: userActions.login,
	logout: userActions.logout
};

const connectedLoginPage = connect(mapState, actionCreators)(Login);
export { connectedLoginPage as Login };
