import React, { Component } from 'react';


class JobDetails extends Component {
    continue = e => {
        e.preventDefault();
        this.props.nextStep();
    }

    back = e => {
        e.preventDefault();
        this.props.prevStep();
    }

    render(){
        const { jobTitle, jobCompany, jobLocation,jobStart, handleChange } = this.props;
        return(
            <div style={{width:'40%',marginLeft:'30%', marginTop:'5%'}}>
		 		<h2 className="text-center">Mortrage Signup</h2>
				 	<form name="form">
						<div>
							<label for="jobtitle">What is your employment status ?</label>
                            <select className="form-control input-shadow"  value={jobTitle} onChange={handleChange('jobTitle')}> 
                                <option name=""> </option>
                                <option name="Buying a home">Employed</option>
                                <option name="Buying a home">Not Employed</option>
                            </select>
                            {/* <input type="text" id="email" 
                                className="form-control input-shadow" 
                                placeholder="Enter job title" 
                                value={jobTitle}
                                onChange={handleChange('jobTitle')}
                            /> */}
							{/* {submitted && !email && <div className="help-block">Email is required</div> } */}
						</div>
						<div >                    
							<label>What is your occupation ? </label>
                            <select className="form-control input-shadow"  value={jobCompany} onChange={handleChange('jobCompany')}> 
                                <option name=""> </option>
                                <option name="Buying a home">Barrister</option>
                            </select>
                            {/* <input type="password" id="exampleInputPassword" 
                                className="form-control input-shadow" 
                                placeholder="Enter company" 
                                value={jobCompany}
                                onChange={handleChange('jobCompany')}
                                 placeholder="Company"
                            /> */}
							{/* {submitted && !email && <div className="help-block">Password is required</div> } */}
						</div>

                        <div >                    
							<label>What is your contract type ? </label>
                            <select className="form-control input-shadow"  value={jobLocation} onChange={handleChange('jobLocation')}> 
                                <option name=""> </option>
                                <option name="Buying a home">Contract</option>
                            </select>
                            {/* <input type="password" id="exampleInputPassword" 
                                className="form-control input-shadow" 
                                placeholder="Enter Location" 
                                onChange={handleChange('jobLocation')}
                                placeholder="Location"
                                 placeholder="Location"
                            /> */}
							{/* {submitted && !email && <div className="help-block">Password is required</div> } */}
						</div>

                        <div >                    
							<label>Do you have full time position with a salary ? <br/>When did you start this job ? </label>

                            <input type="date" 
                                className="form-control input-shadow" 
                                placeholder="Enter the date" 
                                onChange={handleChange('jobStart')}
                                value ={jobStart}

                            />
							{/* {submitted && !email && <div className="help-block">Password is required</div> } */}
						</div>

                        <button type="button" 
                            style={{width:'30%',marginTop:'5%',marginLeft:'30%'}}
                            onClick={this.continue} 
                            className="btn btn-primary btn-block"
                        >Next
                        </button>
						{/* <Link to="/register" className="btn btn-link" style={{fontSize:'16px',marginLeft:'40%'}}>Register</Link> */}
					</form>
			 </div>
            // <>
            //     <h2>Enter your job information:</h2>
            //     <label>
            //         <input 
            //             type="text"
            //             name="jobTitle"
            //             value={jobTitle}
            //             onChange={handleChange('jobTitle')}
            //             placeholder="Job Title"
            //         />
            //     </label>
            //     <label>
            //         <input 
            //             type="text"
            //             name="jobCompany"
            //             value={jobCompany}
            //             onChange={handleChange('jobCompany')}
            //             placeholder="Company"
            //         />
            //     </label>
            //     <label>
            //         <input 
            //             type="text"
            //             name="jobCompany"
            //             value={jobLocation}
            //             onChange={handleChange('jobLocation')}
            //             placeholder="Location"
            //         />
            //     </label>
            //     <button className="Back" onClick={this.back}>
            //         « Back
            //     </button>
            //     <button className="Next" onClick={this.continue}>
            //         Next »
            //     </button>
            // </>
        );
    }
}

export default JobDetails;
