import React, { Component } from 'react';


class JobDetails extends Component {
    continue = e => {
        e.preventDefault();
        this.props.nextStep();
    }

    back = e => {
        e.preventDefault();
        this.props.prevStep();
    }

    render(){
        const { title, firstName, middleName,surname, handleChange } = this.props;
        return(
            <div style={{width:'40%',marginLeft:'30%', marginTop:'5%'}}>
		 		<h2 className="text-center">Mortrage Signup</h2>
				 	<form name="form">
						<div>
							<label for="jobtitle">Title</label>
                            <select className="form-control input-shadow"  value={title} onChange={handleChange('title')}> 
                                <option name=""> </option>
                                <option name="title">title</option>
                            </select>
                            {/* <input type="text" id="email" 
                                className="form-control input-shadow" 
                                placeholder="Enter job title" 
                                value={jobTitle}
                                onChange={handleChange('jobTitle')}
                            /> */}
							{/* {submitted && !email && <div className="help-block">Email is required</div> } */}
						</div>
						<div >                    
							<label>First Name </label>

                            <input type="text" 
                                className="form-control input-shadow" 
                                placeholder="Enter First name" 
                                value={firstName}
                                onChange={handleChange('firstName')}
                            />
							{/* {submitted && !email && <div className="help-block">Password is required</div> } */}
						</div>

                        <div >                    
							<label>Middle Name </label>
                            <input type="text" 
                                className="form-control input-shadow" 
                                onChange={handleChange('middleName')}
                                value={middleName}
                                placeholder="Enter Middlename"
                            />
							{/* {submitted && !email && <div className="help-block">Password is required</div> } */}
						</div>

                        <div >                    
							<label>Surname <br/>When did you start this job ? </label>

                            <input type="text" 
                                className="form-control input-shadow" 
                                placeholder="Enter surname" 
                                onChange={handleChange('surname')}
                                value ={surname}

                            />
							{/* {submitted && !email && <div className="help-block">Password is required</div> } */}
						</div>

                        <button type="button" 
                            style={{width:'30%',marginTop:'5%',marginLeft:'30%'}}
                            onClick={this.continue} 
                            className="btn btn-primary btn-block"
                        >Next
                        </button>
						{/* <Link to="/register" className="btn btn-link" style={{fontSize:'16px',marginLeft:'40%'}}>Register</Link> */}
					</form>
			 </div>
            // <>
            //     <h2>Enter your job information:</h2>
            //     <label>
            //         <input 
            //             type="text"
            //             name="jobTitle"
            //             value={jobTitle}
            //             onChange={handleChange('jobTitle')}
            //             placeholder="Job Title"
            //         />
            //     </label>
            //     <label>
            //         <input 
            //             type="text"
            //             name="jobCompany"
            //             value={jobCompany}
            //             onChange={handleChange('jobCompany')}
            //             placeholder="Company"
            //         />
            //     </label>
            //     <label>
            //         <input 
            //             type="text"
            //             name="jobCompany"
            //             value={jobLocation}
            //             onChange={handleChange('jobLocation')}
            //             placeholder="Location"
            //         />
            //     </label>
            //     <button className="Back" onClick={this.back}>
            //         « Back
            //     </button>
            //     <button className="Next" onClick={this.continue}>
            //         Next »
            //     </button>
            // </>
        );
    }
}

export default JobDetails;
