                            /> */}
							{/* {submitted && !email && <div className="help-block">Password is required</div> } */}
						</div>

                        <div >                    
							<label>How much do you think the propert will cost ? </label>
                            <input type="text" 
                                className="form-control input-shadow" 
                                placeholder="propertycost" 
                                value={propertycost}
                                onChange={handleChange('propertycost')}	
                                name="propertycost"
                            />
							{/* {submitted && !email && <div className="help-block">Password is required</div> } */}
						</div>

                        <div >                    
							<label>Password: </label>
                            <input type="text" 
                                className="form-control input-shadow" 
                                placeholder="No of people" 
                                value={deposit}
                                onChange={handleChange('deposit')}	
                                name="deposit"
                            />
							{/* {submitted && !email && <div className="help-block">Password is required</div> } */}
						</div>

                        <button type="button" 
                        style={{width:'30%',marginTop:'5%',marginLeft:'30%'}}
                            onClick={this.continue} 
                            className="btn btn-primary btn-block"
                        >Next
                        </button>
						{/* <Link to="/register" className="btn btn-link" style={{fontSize:'16px',marginLeft:'40%'}}>Register</Link> */}
					</form>
			 </div>
        );
    }
}

export default PersonalInfo;
